import React, { useState } from "react";
import axios from "axios";

function App() {
  const [selectedValue, setSelectedValue] = useState(""); // State to store dropdown value

  const handleSelectChange = (e) => {
    setSelectedValue(e.target.value); // Update state with selected value
  };

  const handleSubmit = async () => {
    try {
      // Sending selected value to Flask server
      const response = await axios.post("http://127.0.0.1:5000/submit", {
        selectedValue,
      });
      console.log("Response from Flask:", response.data);
    } catch (error) {
      console.error("Error sending data to Flask:", error);
    }
  };

  return (
    <div>
      <h1>React Dropdown Menu</h1>
      <select value={selectedValue} onChange={handleSelectChange}>
        <option value="" disabled>Select an option</option>
        <option value="Option 1">Option 1</option>
        <option value="Option 2">Option 2</option>
        <option value="Option 3">Option 3</option>
      </select>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default App;
